import { ADD_ONE, APPLY_NUMBER, CHANGE_OPERATION, STORE_MEMORY, MEMORY_RESET, MEMORY_TOTAL } from './../actions';

export const initialState = {
  total: 101,
  operation: "*",
  memory: 99
}

const calculateResult = (num1, num2, operation) => {
  switch (operation) {
    case ("+"):
      return num1 + num2;
    case ("*"):
      return num1 * num2;
    case ("-"):
      return num1 - num2;
    default:
      return;
  }
}

const reducer = (state, action) => {
  switch (action.type) {
    case (ADD_ONE):
      return ({
        ...state,
        total: state.total + 1
      });

    case (APPLY_NUMBER):
      return ({
        ...state,
        total: calculateResult(state.total, Number(action.payload), state.operation)
      });

    case (CHANGE_OPERATION):
      return ({
        ...state,
        operation: action.payload
      });

      case (STORE_MEMORY):
      return ({
        ...state,
        memory: state.total,
      });

      case (MEMORY_TOTAL):
      return ({
        ...state,
        total: calculateResult(state.total, state.memory, state.operation)
      });

      case (MEMORY_RESET):
      return ({
        ...state,
        memory: initialState.memory,
      });

    default:
      return state;
  }
}

export default reducer;
